mapper.select:

select blog_id, user_id, content, video_url, type, create_time, publish_time, is_original
from blog
where blog_id = #{blogId,jdbcType=BIGINT}

select blog_id, user_id, content, video_url, type, create_time, publish_time, is_original
from blog
where 1 =1

  and  user_id = #{userId}



  and  type = #{type,jdbcType=BIGINT}

 order by publish_time desc
 limit #{start},#{count}
