mapper.select:

select id, user_id, sign_history, sign_count, year, last_sign_time
from sign
where id = #{id,jdbcType=BIGINT}

mapper.update:
update sign
set user_id = #{sign.userId,jdbcType=BIGINT},
sign_history = #{sign.signHistory,jdbcType=VARCHAR},
sign_count = #{sign.signCount,jdbcType=INTEGER},
last_sign_time = #{sign.lastSignTime,jdbcType=TIMESTAMP}
where user_id = #{sign.userId,jdbcType=BIGINT} and year = #{year}

mapper.select:
select id, user_id, sign_history, sign_count, year, last_sign_time
from sign
where user_id = #{userId,jdbcType=BIGINT} and year = #{year}
